import singleNodeCollectionValidator from "../validators/singleNodeCollection.finder.validator.js";
export default function switchFinder(jcs, collection, { name }) {
    const split = name.split(".");
    return singleNodeCollectionValidator(collection
        .find(jcs.Identifier, { name: split[split.length - 1] })
        .closest(jcs.SwitchStatement), name);
}
