import { DebugLogger } from "../../utils/Logger.js";
const log = DebugLogger("switchCase.inject.stage.ts");
export default function injectSwitchCaseStage(jcs, workingSource, { caseName, statements, col, identifier }) {
    if (!col) {
        log("error", "No expression collection passed to this stage.");
        return workingSource;
    }
    const newCase = jcs.switchCase(identifier ? jcs.identifier(caseName) : jcs.literal(caseName), statements);
    const switchStatement = col.get().value;
    switchStatement.cases.unshift(newCase);
    col.forEach(p => p.replace(switchStatement));
    return workingSource;
}
