import { chalkStderr } from "chalk";
export default function singleNodeCollectionValidator(collection, collectionName) {
    const col = collection.filter((e, i) => i === 0);
    if (!col.size())
        throw new Error(chalkStderr.redBright(`The collection ${collectionName} is empty, check your injection pipeline finder options. If everything looks good, there might be an issue with the finder function itself.`));
    return { col, idName: collectionName };
}
