import programFinder from "../pipeline/finders/program.finder.js";
import jcs from "jscodeshift";
export default function injectToProgram(ast, injections) {
    const programCol = programFinder(jcs, ast);
    programCol.forEach(path => {
        injections.forEach(({ statement, injectionLine }) => {
            injectionLine === "first"
                ? path.node.body.unshift(statement)
                : path.node.body.push(statement);
        });
    });
}
