import programFinder from "../pipeline/finders/program.finder.js";
import jcs from "jscodeshift";
/**
 *
 * @param string - Provide code program in string format
 * @example
 * ```
 * `
 *  const helloResponse = "world";
 *
 *  function hello(){
 *    console.log(helloResponse);
 *  }
 * `
 * ```
 */
export default function stringToStatements(str) {
    const ast = jcs.withParser("tsx")(str);
    const programCol = programFinder(jcs, ast);
    let statements = [];
    programCol
        .paths()[0]
        .node.body.forEach(statement => statements.push(statement));
    return statements;
}
